import React, { useState, useEffect } from 'react';
import { Configuration, OpenAIApi } from 'openai';
import axios from 'axios';
import './Chatbot.css';

function Chatbot({ apiKey, models, className }) {
  const [message, setMessage] = useState('');
  const [conversation, setConversation] = useState([]);
  const [model, setModel] = useState(models[0].value);

  // Initialize the OpenAI API object with the default model
  const [openai, setOpenai] = useState(null);
  useEffect(() => {
    const configuration = new Configuration({
      apiKey: apiKey,
    });
    const initialModel = models[0].value;
    const initialOpenai = new OpenAIApi(configuration);
    setOpenai(initialOpenai);
    setModel(initialModel);
  }, [apiKey, models]);

  const handleModelChange = (event) => {
    const selectedModel = event.target.value;
    const selectedModelConfig = models.find((model) => model.value === selectedModel);
    const configuration = new Configuration({
      apiKey: apiKey,
      modelId: selectedModelConfig.id,
    });
    const selectedOpenai = new OpenAIApi(configuration);
    setOpenai(selectedOpenai);
    setModel(selectedModel);
    setConversation([]);
  };

  const sendMessage = async () => {
    // Add the user's message to the conversation
    setConversation((prevConversation) => [
      ...prevConversation,
      {
        text: message,
        sender: 'user',
      },
    ]);

    // Send the user's message to the API
    const completion = await openai.createChatCompletion({
      prompt: message,
      maxTokens: 60,
      temperature: 0.7,
      n: 1,
      stop: "\n",
    });

    // Add the bot's response to the conversation
    setConversation((prevConversation) => [
      ...prevConversation,
      {
        text: completion.data.choices[0].text,
        sender: 'bot',
      },
    ]);

    // Clear the message input
    setMessage('');
  };

  return (
    <div className={className}>
      <div>
        <select value={model} onChange={handleModelChange}>
          {models.map((model) => (
            <option key={model.value} value={model.value}>
              {model.label}
            </option>
          ))}
        </select>
      </div>
      <div className="conversation">
        {conversation.map((message, index) => (
          <div key={index}>
            {message.sender === 'user' && <div className="user-message">You: {message.text}</div>}
            {message.sender === 'bot' && <div className="bot-message">Bot: {message.text}</div>}
          </div>
        ))}
      </div>
      <div className="input-message-box">
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyPress={(e) => {
            if (e.key === 'Enter') {
              sendMessage();
            }
          }}
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
}

export default Chatbot;
